INSERT INTO `tbl_uu` (`id_tbl_uu`, `uu`, `tentang`) VALUES
(1, '28 TAHUN 2009', 'PAJAK DAERAH DAN RETRIBUSI DAERAH');
INSERT INTO `tbl_uu_pasal` (`id`, `id_tbl_uu`, `uud_id`, `uud_section`, `uud_content`) VALUES
(1, 1, 'judul', 'judul', '<b>UNDANG-UNDANG<BR />NOMOR 28 TAHUN 2009<BR />TENTANG<BR /> PAJAK DAERAH DAN RETRIBUSI DAERAH</b>'),
(2, 1, 'irah-irah', 'irah-irah', 'DENGAN RAHMAT TUHAN YANG MAHA ESA'),
(3, 1, 'irah-irah', 'irah-irah', 'PRESIDEN REPUBLIK INDONESIA'),
(4, 1, 'menimbang huruf~a', 'huruf', 'bahwa Negara Kesatuan Republik Indonesia merupakan negara hukum berdasarkan Pancasila dan Undang-Undang Dasar Negara Republik Indonesia Tahun 1945, bertujuan untuk mewujudkan tata kehidupan bangsa yang aman, tertib, sejahtera, dan berkeadilan;'),
(5, 1, 'menimbang huruf~b', 'huruf', 'bahwa dengan berlakunya Undang-Undang Nomor 32 Tahun 2004 tentang Pemerintahan Daerah sebagaimana telah diubah beberapa kali terakhir dengan Undang-Undang Nomor 12 Tahun 2008 tentang Perubahan Kedua atas Undang-Undang Nomor 32 Tahun 2004 tentang Pemerintahan Daerah dan Undang-Undang Nomor 33 Tahun 2004 tentang Perimbangan Keuangan antara Pemerintah Pusat dan Pemerintahan Daerah, maka penyelenggaraan pemerintahan daerah dilakukan dengan memberikan kewenangan yang seluas-luasnya, disertai dengan pemberian hak dan kewajiban menyelenggarakan otonomi daerah dalam kesatuan sistem penyelenggaraan pemerintahan negara;'),
(6, 1, 'menimbang huruf~c', 'huruf', 'bahwa pajak daerah dan retribusi daerah merupakan salah satu sumber pendapatan daerah yang penting guna membiayai pelaksanaan pemerintahan daerah;'),
(7, 1, 'menimbang huruf~d', 'huruf', 'bahwa dalam rangka meningkatkan pelayanan kepada masyarakat dan kemandirian daerah, perlu dilakukan perluasan objek pajak daerah dan retribusi daerah dan pemberian diskresi dalam penetapan tarif;'),
(8, 1, 'menimbang huruf~e', 'huruf', 'bahwa kebijakan pajak daerah dan retribusi daerah dilaksanakan berdasarkan prinsip demokrasi, pemerataan dan keadilan, peran serta masyarakat, dan akuntabilitas dengan memperhatikan potensi daerah;'),
(9, 1, 'menimbang huruf~f', 'huruf', 'bahwa Undang-Undang Nomor 18 Tahun 1997 tentang Pajak Daerah dan Retribusi Daerah sebagaimana telah diubah dengan Undang-Undang Nomor 34 Tahun 2000 tentang Perubahan atas Undang-Undang Nomor 18 Tahun 1997 tentang Pajak Daerah dan Retribusi Daerah, perlu disesuaikan dengan kebijakan otonomi daerah;'),
(10, 1, 'menimbang huruf~g', 'huruf', 'bahwa berdasarkan pertimbangan sebagaimana dimaksud dalam huruf a, huruf b, huruf c, huruf d, huruf e, dan huruf f, perlu membentuk Undang-Undang tentang Pajak Daerah dan Retribusi Daerah.'),
(11, 1, 'mengingat', 'mengingat', 'Pasal 5 ayat (1), Pasal 18, Pasal 18A, Pasal 18B, Pasal 20 ayat (2), Pasal 22D, dan Pasal 23A Undang-Undang Dasar Negara Republik Indonesia Tahun 1945.'),
(12, 1, 'persetujuan', 'persetujuan', 'Dengan Persetujuan Bersama'),
(13, 1, 'persetujuan', 'persetujuan', 'DEWAN PERWAKILAN RAKYAT REPUBLIK INDONESIA'),
(14, 1, 'persetujuan', 'persetujuan', 'dan'),
(15, 1, 'persetujuan', 'persetujuan', 'PRESIDEN REPUBLIK INDONESIA'),
(16, 1, 'menetapkan', 'menetapkan', 'UNDANG-UNDANG TENTANG PAJAK DAERAH DAN RETRIBUSI DAERAH.'),
(17, 1, 'bab~i pasal~1', 'pasal', 'Dalam undang-undang ini yang dimaksud dengan:'),
(18, 1, 'bab~i pasal~1 angka~1', 'angka', 'Daerah Otonom, yang selanjutnya disebut Daerah, adalah kesatuan masyarakat hukum yang mempunyai batas-batas wilayah yang berwenang mengatur dan mengurus urusan pemerintahan dan kepentingan masyarakat setempat menurut prakarsa sendiri berdasarkan aspirasi masyarakat dalam sistem Negara Kesatuan Republik Indonesia.'),
(19, 1, 'bab~i pasal~1 angka~2', 'angka', 'Pemerintah Pusat, yang selanjutnya disebut Pemerintah, adalah Presiden Republik Indonesia yang memegang kekuasaan pemerintahan negara Republik Indonesia sebagaimana dimaksud dalam Undang- Undang Dasar Negara Republik Indonesia Tahun 1945.'),
(20, 1, 'bab~i pasal~1 angka~3', 'angka', 'Pemerintahan Daerah adalah penyelenggaraan urusan pemerintahan oleh Pemerintah Daerah dan Dewan Perwakilan Rakyat Daerah menurut asas otonomi dan Tugas Pembantuan dengan prinsip otonomi seluas-luasnya dalam sistem dan prinsip Negara Kesatuan Republik Indonesia sebagaimana dimaksud dalam Undang-Undang Dasar Negara Republik Indonesia Tahun 1945.'),
(21, 1, 'bab~i pasal~1 angka~4', 'angka', 'Pemerintah Daerah adalah gubernur, bupati, atau walikota, dan perangkat Daerah sebagai unsur penyelenggara Pemerintahan Daerah.'),
(22, 1, 'bab~i pasal~1 angka~5', 'angka', 'Dewan Perwakilan Rakyat Daerah, yang selanjutnya disingkat DPRD, adalah lembaga perwakilan rakyat daerah sebagai unsur penyelenggara Pemerintahan Daerah.'),
(23, 1, 'bab~i pasal~1 angka~6', 'angka', 'Kepala Daerah adalah gubernur bagi Daerah provinsi atau bupati bagi Daerah kabupaten atau walikota bagi Daerah kota.'),
(24, 1, 'bab~i pasal~1 angka~7', 'angka', 'Pejabat adalah pegawai yang diberi tugas tertentu di bidang perpajakan daerah dan/atau retribusi daerah sesuai dengan peraturan perundang-undangan.'),
(25, 1, 'bab~i pasal~1 angka~8', 'angka', 'Peraturan Daerah adalah peraturan perundang-undangan yang dibentuk oleh DPRD provinsi dan/atau daerah kabupaten/kota dengan persetujuan bersama Kepala Daerah.'),
(26, 1, 'bab~i pasal~1 angka~9', 'angka', 'Peraturan Kepala Daerah adalah Peraturan Gubernur dan/atau Peraturan Bupati/Walikota.'),
(27, 1, 'bab~i pasal~1 angka~10', 'angka', 'Pajak Daerah, yang selanjutnya disebut Pajak, adalah kontribusi wajib kepada Daerah yang terutang oleh orang pribadi atau badan yang bersifat memaksa berdasarkan Undang-Undang, dengan tidak mendapatkan imbalan secara langsung dan digunakan untuk keperluan Daerah bagi sebesar-besarnya kemakmuran rakyat.'),
(28, 1, 'bab~i pasal~1 angka~11', 'angka', 'Badan adalah sekumpulan orang dan/atau modal yang merupakan kesatuan, baik yang melakukan usaha maupun yang tidak melakukan usaha yang meliputi perseroan terbatas, perseroan komanditer, perseroan lainnya, badan usaha milik negara (BUMN), atau badan usaha milik daerah (BUMD) dengan nama dan dalam bentuk apa pun, firma, kongsi, koperasi, dana pensiun, persekutuan, perkumpulan, yayasan, organisasi massa, organisasi sosial politik, atau organisasi lainnya, lembaga dan bentuk badan lainnya termasuk kontrak investasi kolektif dan bentuk usaha tetap.'),
(29, 1, 'bab~i pasal~1 angka~12', 'angka', 'Pajak Kendaraan Bermotor adalah pajak atas kepemilikan dan/atau penguasaan kendaraan bermotor.'),
(30, 1, 'bab~i pasal~1 angka~13', 'angka', 'Kendaraan Bermotor adalah semua kendaraan beroda beserta gandengannya yang digunakan di semua jenis jalan darat, dan digerakkan oleh peralatan teknik berupa motor atau peralatan lainnya yang berfungsi untuk mengubah suatu sumber daya energi tertentu menjadi tenaga gerak kendaraan bermotor yang bersangkutan, termasuk alat-alat berat dan alat-alat besar yang dalam operasinya menggunakan roda dan motor dan tidak melekat secara permanen serta kendaraan bermotor yang dioperasikan di air.'),
(31, 1, 'bab~i pasal~1 angka~14', 'angka', 'Bea Balik Nama Kendaraan Bermotor adalah pajak atas penyerahan hak milik kendaraan bermotor sebagai akibat perjanjian dua pihak atau perbuatan sepihak atau keadaan yang terjadi karena jual beli, tukar menukar, hibah, warisan, atau pemasukan ke dalam badan usaha.'),
(32, 1, 'bab~i pasal~1 angka~15', 'angka', 'Pajak Bahan Bakar Kendaraan Bermotor adalah pajak atas penggunaan bahan bakar kendaraan bermotor.'),
(33, 1, 'bab~i pasal~1 angka~16', 'angka', 'Bahan Bakar Kendaraan Bermotor adalah semua jenis bahan bakar cair atau gas yang digunakan untuk kendaraan bermotor.'),
(34, 1, 'bab~i pasal~1 angka~17', 'angka', 'Pajak Air Permukaan adalah pajak atas pengambilan dan/atau pemanfaatan air permukaan.'),
(35, 1, 'bab~i pasal~1 angka~18', 'angka', 'Air Permukaan adalah semua air yang terdapat pada permukaan tanah, tidak termasuk air laut, baik yang berada di laut maupun di darat.'),
(36, 1, 'bab~i pasal~1 angka~19', 'angka', 'Pajak Rokok adalah pungutan atas cukai rokok yang dipungut oleh Pemerintah.'),
(37, 1, 'bab~i pasal~1 angka~20', 'angka', 'Pajak Hotel adalah pajak atas pelayanan yang disediakan oleh hotel.'),
(38, 1, 'bab~i pasal~1 angka~21', 'angka', 'Hotel adalah fasilitas penyedia jasa penginapan/peristirahatan termasuk jasa terkait lainnya dengan dipungut bayaran, yang mencakup juga motel, losmen, gubuk pariwisata, wisma pariwisata, pesanggrahan, rumah penginapan dan sejenisnya, serta rumah kos dengan jumlah kamar lebih dari 10 (sepuluh).'),
(39, 1, 'bab~i pasal~1 angka~22', 'angka', 'Pajak Restoran adalah pajak atas pelayanan yang disediakan oleh restoran.'),
(40, 1, 'bab~i pasal~1 angka~23', 'angka', 'Restoran adalah fasilitas penyedia makanan dan/atau minuman dengan dipungut bayaran, yang mencakup juga rumah makan, kafetaria, kantin, warung, bar, dan sejenisnya termasuk jasa boga/katering.'),
(41, 1, 'bab~i pasal~1 angka~24', 'angka', 'Pajak Hiburan adalah pajak atas penyelenggaraan hiburan.'),
(42, 1, 'bab~i pasal~1 angka~25', 'angka', 'Hiburan adalah semua jenis tontonan, pertunjukan, permainan, dan/atau keramaian yang dinikmati dengan dipungut bayaran.'),
(43, 1, 'bab~i pasal~1 angka~26', 'angka', 'Pajak Reklame adalah pajak atas penyelenggaraan reklame.'),
(44, 1, 'bab~i pasal~1 angka~27', 'angka', 'Reklame adalah benda, alat, perbuatan, atau media yang bentuk dan corak ragamnya dirancang untuk tujuan komersial memperkenalkan, menganjurkan, mempromosikan, atau untuk menarik perhatian umum terhadap barang, jasa, orang, atau badan, yang dapat dilihat, dibaca, didengar, dirasakan, dan/atau dinikmati oleh umum.'),
(45, 1, 'bab~i pasal~1 angka~28', 'angka', 'Pajak Penerangan Jalan adalah pajak atas penggunaan tenaga listrik, baik yang dihasilkan sendiri maupun diperoleh dari sumber lain.'),
(46, 1, 'bab~i pasal~1 angka~29', 'angka', 'Pajak Mineral Bukan Logam dan Batuan adalah pajak atas kegiatan pengambilan mineral bukan logam dan batuan, baik dari sumber alam di dalam dan/atau permukaan bumi untuk dimanfaatkan.'),
(47, 1, 'bab~i pasal~1 angka~30', 'angka', 'Mineral Bukan Logam dan Batuan adalah mineral bukan logam dan batuan sebagaimana dimaksud di dalam peraturan perundang-undangan di bidang mineral dan batubara.'),
(48, 1, 'bab~i pasal~1 angka~31', 'angka', 'Pajak Parkir adalah pajak atas penyelenggaraan tempat parkir di luar badan jalan, baik yang disediakan berkaitan dengan pokok usaha maupun yang disediakan sebagai suatu usaha, termasuk penyediaan tempat penitipan kendaraan bermotor.'),
(49, 1, 'bab~i pasal~1 angka~32', 'angka', 'Parkir adalah keadaan tidak bergerak suatu kendaraan yang tidak bersifat sementara.'),
(50, 1, 'bab~i pasal~1 angka~33', 'angka', 'Pajak Air Tanah adalah pajak atas pengambilan dan/atau pemanfaatan air tanah.'),
(51, 1, 'bab~i pasal~1 angka~34', 'angka', 'Air Tanah adalah air yang terdapat dalam lapisan tanah atau batuan di bawah permukaan tanah.'),
(52, 1, 'bab~i pasal~1 angka~35', 'angka', 'Pajak Sarang Burung Walet adalah pajak atas kegiatan pengambilan dan/atau pengusahaan sarang burung walet.'),
(53, 1, 'bab~i pasal~1 angka~36', 'angka', 'Burung Walet adalah satwa yang termasuk marga collocalia, yaitu collocalia fuchliap haga, collocalia maxina, collocalia esculanta, dan collocalia linchi.'),
(54, 1, 'bab~i pasal~1 angka~37', 'angka', 'Pajak Bumi dan Bangunan Perdesaan dan Perkotaan adalah pajak atas bumi dan/atau bangunan yang dimiliki, dikuasai, dan/atau dimanfaatkan oleh orang pribadi atau Badan, kecuali kawasan yang digunakan untuk kegiatan usaha perkebunan, perhutanan, dan pertambangan.'),
(55, 1, 'bab~i pasal~1 angka~38', 'angka', 'Bumi adalah permukaan bumi yang meliputi tanah dan perairan pedalaman serta laut wilayah kabupaten/kota.'),
(56, 1, 'bab~i pasal~1 angka~39', 'angka', 'Bangunan adalah konstruksi teknik yang ditanam atau dilekatkan secara tetap pada tanah dan/atau perairan pedalaman dan/atau laut.'),
(57, 1, 'bab~i pasal~1 angka~40', 'angka', 'Nilai Jual Objek Pajak, yang selanjutnya disingkat NJOP, adalah harga rata-rata yang diperoleh dari transaksi jual beli yang terjadi secara wajar, dan bilamana tidak terdapat transaksi jual beli, NJOP ditentukan melalui perbandingan harga dengan objek lain yang sejenis, atau nilai perolehan baru, atau NJOP pengganti.'),
(58, 1, 'bab~i pasal~1 angka~41', 'angka', 'Bea Perolehan Hak atas Tanah dan Bangunan adalah pajak atas perolehan hak atas tanah dan/atau bangunan.'),
(59, 1, 'bab~i pasal~1 angka~42', 'angka', 'Perolehan Hak atas Tanah dan/atau Bangunan adalah perbuatan atau peristiwa hukum yang mengakibatkan diperolehnya hak atas tanah dan/atau bangunan oleh orang pribadi atau Badan.'),
(60, 1, 'bab~i pasal~1 angka~43', 'angka', 'Hak atas Tanah dan/atau Bangunan adalah hak atas tanah, termasuk hak pengelolaan, beserta bangunan di atasnya, sebagaimana dimaksud dalam undang-undang di bidang pertanahan dan bangunan.'),
(61, 1, 'bab~i pasal~1 angka~44', 'angka', 'Subjek Pajak adalah orang pribadi atau Badan yang dapat dikenakan Pajak.'),
(62, 1, 'bab~i pasal~1 angka~45', 'angka', 'Wajib Pajak adalah orang pribadi atau Badan, meliputi pembayar pajak, pemotong pajak, dan pemungut pajak, yang mempunyai hak dan kewajiban perpajakan sesuai dengan ketentuan peraturan perundang- undangan perpajakan daerah.'),
(63, 1, 'bab~i pasal~1 angka~46', 'angka', 'Masa Pajak adalah jangka waktu 1 (satu) bulan kalender atau jangka waktu lain yang diatur dengan Peraturan Kepala Daerah paling lama 3 (tiga) bulan kalender, yang menjadi dasar bagi Wajib Pajak untuk menghitung, menyetor, dan melaporkan pajak yang terutang.'),
(64, 1, 'bab~i pasal~1 angka~47', 'angka', 'Tahun Pajak adalah jangka waktu yang lamanya 1 (satu) tahun kalender, kecuali bila Wajib Pajak menggunakan tahun buku yang tidak sama dengan tahun kalender.'),
(65, 1, 'bab~i pasal~1 angka~48', 'angka', 'Pajak yang terutang adalah pajak yang harus dibayar pada suatu saat, dalam Masa Pajak, dalam Tahun Pajak, atau dalam Bagian Tahun Pajak sesuai dengan ketentuan peraturan perundang-undangan perpajakan daerah.'),
(66, 1, 'bab~i pasal~1 angka~49', 'angka', 'Pemungutan adalah suatu rangkaian kegiatan mulai dari penghimpunan data objek dan subjek pajak atau retribusi, penentuan besarnya pajak atau retribusi yang terutang sampai kegiatan penagihan pajak atau retribusi kepada Wajib Pajak atau Wajib Retribusi serta pengawasan penyetorannya.'),
(67, 1, 'bab~i pasal~1 angka~50', 'angka', 'Surat Pemberitahuan Pajak Daerah, yang selanjutnya disingkat SPTPD, adalah surat yang oleh Wajib Pajak digunakan untuk melaporkan penghitungan dan/atau pembayaran pajak, objek pajak dan/atau bukan objek pajak, dan/atau harta dan kewajiban sesuai dengan ketentuan peraturan perundang- undangan perpajakan daerah.'),
(68, 1, 'bab~i pasal~1 angka~51', 'angka', 'Surat Pemberitahuan Objek Pajak, yang selanjutnya disingkat SPOP, adalah surat yang digunakan oleh Wajib Pajak untuk melaporkan data subjek dan objek Pajak Bumi dan Bangunan Perdesaan dan Perkotaan sesuai dengan ketentuan peraturan perundang-undangan perpajakan daerah.'),
(69, 1, 'bab~i pasal~1 angka~52', 'angka', 'Surat Setoran Pajak Daerah, yang selanjutnya disingkat SSPD, adalah bukti pembayaran atau penyetoran pajak yang telah dilakukan dengan menggunakan formulir atau telah dilakukan dengan cara lain ke kas daerah melalui tempat pembayaran yang ditunjuk oleh Kepala Daerah.'),
(70, 1, 'bab~i pasal~1 angka~53', 'angka', 'Surat Ketetapan Pajak Daerah, yang selanjutnya disingkat SKPD, adalah surat ketetapan pajak yang menentukan besarnya jumlah pokok pajak yang terutang.'),
(71, 1, 'bab~i pasal~1 angka~54', 'angka', 'Surat Pemberitahuan Pajak Terutang, yang selanjutnya disingkat SPPT, adalah surat yang digunakan untuk memberitahukan besarnya Pajak Bumi dan Bangunan Perdesaan dan Perkotaan yang terutang kepada Wajib Pajak.'),
(72, 1, 'bab~i pasal~1 angka~55', 'angka', 'Surat Ketetapan Pajak Daerah Kurang Bayar, yang selanjutnya disingkat SKPDKB, adalah surat ketetapan pajak yang menentukan besarnya jumlah pokok pajak, jumlah kredit pajak, jumlah kekurangan pembayaran pokok pajak, besarnya sanksi administratif, dan jumlah pajak yang masih harus dibayar.'),
(73, 1, 'bab~i pasal~1 angka~56', 'angka', 'Surat Ketetapan Pajak Daerah Kurang Bayar Tambahan, yang selanjutnya disingkat SKPDKBT, adalah surat ketetapan pajak yang menentukan tambahan atas jumlah pajak yang telah ditetapkan.'),
(74, 1, 'bab~i pasal~1 angka~57', 'angka', 'Surat Ketetapan Pajak Daerah Nihil, yang selanjutnya disingkat SKPDN, adalah surat ketetapan pajak yang menentukan jumlah pokok pajak sama besarnya dengan jumlah kredit pajak atau pajak tidak terutang dan tidak ada kredit pajak.'),
(75, 1, 'bab~i pasal~1 angka~58', 'angka', 'Surat Ketetapan Pajak Daerah Lebih Bayar, yang selanjutnya disingkat SKPDLB, adalah surat ketetapan pajak yang menentukan jumlah kelebihan pembayaran pajak karena jumlah kredit pajak lebih besar daripada pajak yang terutang atau seharusnya tidak terutang.'),
(76, 1, 'bab~i pasal~1 angka~59', 'angka', 'Surat Tagihan Pajak Daerah, yang selanjutnya disingkat STPD, adalah surat untuk melakukan tagihan pajak dan/atau sanksi administratif berupa bunga dan/atau denda.'),
(77, 1, 'bab~i pasal~1 angka~60', 'angka', 'Surat Keputusan Pembetulan adalah surat keputusan yang membetulkan kesalahan tulis, kesalahan hitung, dan/atau kekeliruan dalam penerapan ketentuan tertentu dalam peraturan perundang-undangan perpajakan daerah yang terdapat dalam Surat Pemberitahuan Pajak Terutang, Surat Ketetapan Pajak Daerah, Surat Ketetapan Pajak Daerah Kurang Bayar, Surat Ketetapan Pajak Daerah Kurang Bayar Tambahan, Surat Ketetapan Pajak Daerah Nihil, Surat Ketetapan Pajak Daerah Lebih Bayar, Surat Tagihan Pajak Daerah, Surat Keputusan Pembetulan, atau Surat Keputusan Keberatan.'),
(78, 1, 'bab~i pasal~1 angka~61', 'angka', 'Surat Keputusan Keberatan adalah surat keputusan atas keberatan terhadap Surat Pemberitahuan Pajak Terutang, Surat Ketetapan Pajak Daerah, Surat Ketetapan Pajak Daerah Kurang Bayar, Surat Ketetapan Pajak Daerah Kurang Bayar Tambahan, Surat Ketetapan Pajak Daerah Nihil, Surat Ketetapan Pajak Daerah Lebih Bayar, atau terhadap pemotongan atau pemungutan oleh pihak ketiga yang diajukan oleh Wajib Pajak.'),
(79, 1, 'bab~i pasal~1 angka~62', 'angka', 'Putusan Banding adalah putusan badan peradilan pajak atas banding terhadap Surat Keputusan Keberatan yang diajukan oleh Wajib Pajak.'),
(80, 1, 'bab~i pasal~1 angka~63', 'angka', 'Pembukuan adalah suatu proses pencatatan yang dilakukan secara teratur untuk mengumpulkan data dan informasi keuangan yang meliputi harta, kewajiban, modal, penghasilan dan biaya, serta jumlah harga perolehan dan penyerahan barang atau jasa, yang ditutup dengan menyusun laporan keuangan berupa neraca dan laporan laba rugi untuk periode Tahun Pajak tersebut.'),
(81, 1, 'bab~i pasal~1 angka~64', 'angka', 'Retribusi Daerah, yang selanjutnya disebut Retribusi, adalah pungutan Daerah sebagai pembayaran atas jasa atau pemberian izin tertentu yang khusus disediakan dan/atau diberikan oleh Pemerintah Daerah untuk kepentingan orang pribadi atau Badan.'),
(82, 1, 'bab~i pasal~1 angka~65', 'angka', 'Jasa adalah kegiatan Pemerintah Daerah berupa usaha dan pelayanan yang menyebabkan barang, fasilitas, atau kemanfaatan lainnya yang dapat dinikmati oleh orang pribadi atau Badan.'),
(83, 1, 'bab~i pasal~1 angka~66', 'angka', 'Jasa Umum adalah jasa yang disediakan atau diberikan oleh Pemerintah Daerah untuk tujuan kepentingan dan kemanfaatan umum serta dapat dinikmati oleh orang pribadi atau Badan.'),
(84, 1, 'bab~i pasal~1 angka~67', 'angka', 'Jasa Usaha adalah jasa yang disediakan oleh Pemerintah Daerah dengan menganut prinsip-prinsip komersial karena pada dasarnya dapat pula disediakan oleh sektor swasta.'),
(85, 1, 'bab~i pasal~1 angka~68', 'angka', 'Perizinan Tertentu adalah kegiatan tertentu Pemerintah Daerah dalam rangka pemberian izin kepada orang pribadi atau Badan yang dimaksudkan untuk pembinaan, pengaturan, pengendalian dan pengawasan atas kegiatan, pemanfaatan ruang, serta penggunaan sumber daya alam, barang, prasarana, sarana atau fasilitas tertentu guna melindungi kepentingan umum dan menjaga kelestarian lingkungan.'),
(86, 1, 'bab~i pasal~1 angka~69', 'angka', 'Wajib Retribusi adalah orang pribadi atau Badan yang menurut peraturan perundang-undangan retribusi diwajibkan untuk melakukan pembayaran retribusi, termasuk pemungut atau pemotong retribusi tertentu.'),
(87, 1, 'bab~i pasal~1 angka~70', 'angka', 'Masa Retribusi adalah suatu jangka waktu tertentu yang merupakan batas waktu bagi Wajib Retribusi untuk memanfaatkan jasa dan perizinan tertentu dari Pemerintah Daerah yang bersangkutan.'),
(88, 1, 'bab~i pasal~1 angka~71', 'angka', 'Surat Setoran Retribusi Daerah, yang selanjutnya disingkat SSRD, adalah bukti pembayaran atau penyetoran retribusi yang telah dilakukan dengan menggunakan formulir atau telah dilakukan dengan cara lain ke kas daerah melalui tempat pembayaran yang ditunjuk oleh Kepala Daerah.'),
(89, 1, 'bab~i pasal~1 angka~72', 'angka', 'Surat Ketetapan Retribusi Daerah, yang selanjutnya disingkat SKRD, adalah surat ketetapan retribusi yang menentukan besarnya jumlah pokok retribusi yang terutang.'),
(90, 1, 'bab~i pasal~1 angka~73', 'angka', 'Surat Ketetapan Retribusi Daerah Lebih Bayar, yang selanjutnya disingkat SKRDLB, adalah surat ketetapan retribusi yang menentukan jumlah kelebihan pembayaran retribusi karena jumlah kredit retribusi lebih besar daripada retribusi yang terutang atau seharusnya tidak terutang.'),
(91, 1, 'bab~i pasal~1 angka~74', 'angka', 'Surat Tagihan Retribusi Daerah, yang selanjutnya disingkat STRD, adalah surat untuk melakukan tagihan retribusi dan/atau sanksi administratif berupa bunga dan/atau denda.'),
(92, 1, 'bab~i pasal~1 angka~75', 'angka', 'Pemeriksaan adalah serangkaian kegiatan menghimpun dan mengolah data, keterangan, dan/atau bukti yang dilaksanakan secara objektif dan profesional berdasarkan suatu standar pemeriksaan untuk menguji kepatuhan pemenuhan kewajiban perpajakan daerah dan retribusi dan/atau untuk tujuan lain dalam rangka melaksanakan ketentuan peraturan perundang-undangan perpajakan daerah dan retribusi daerah.'),
(93, 1, 'bab~i pasal~1 angka~76', 'angka', 'Penyidikan tindak pidana di bidang perpajakan daerah dan retribusi adalah serangkaian tindakan yang dilakukan oleh Penyidik untuk mencari serta mengumpulkan bukti yang dengan bukti itu membuat terang tindak pidana di bidang perpajakan daerah dan retribusi yang terjadi serta menemukan tersangkanya.'),
(94, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~1', 'ayat', 'Jenis Pajak provinsi terdiri atas:'),
(95, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~1 huruf~a', 'huruf', 'Pajak Kendaraan Bermotor;'),
(96, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~1 huruf~b', 'huruf', 'Bea Balik Nama Kendaraan Bermotor;'),
(97, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~1 huruf~c', 'huruf', 'Pajak Bahan Bakar Kendaraan Bermotor;'),
(98, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~1 huruf~d', 'huruf', 'Pajak Air Permukaan; dan'),
(99, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~1 huruf~e', 'huruf', 'Pajak Rokok.'),
(100, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~2', 'ayat', 'Jenis Pajak kabupaten/kota terdiri atas:'),
(101, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~2 huruf~a', 'huruf', 'Pajak Hotel;'),
(102, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~2 huruf~b', 'huruf', 'Pajak Restoran;'),
(103, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~2 huruf~c', 'huruf', 'Pajak Hiburan;'),
(104, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~2 huruf~d', 'huruf', 'Pajak Reklame;'),
(105, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~2 huruf~e', 'huruf', 'Pajak Penerangan Jalan;'),
(106, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~2 huruf~f', 'huruf', 'Pajak Mineral Bukan Logam dan Batuan;'),
(107, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~2 huruf~g', 'huruf', 'Pajak Parkir;'),
(108, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~2 huruf~h', 'huruf', 'Pajak Air Tanah;'),
(109, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~2 huruf~i', 'huruf', 'Pajak Sarang Burung Walet;'),
(110, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~2 huruf~j', 'huruf', 'Pajak Bumi dan Bangunan Perdesaan dan Perkotaan; dan'),
(111, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~2 huruf~k', 'huruf', 'Bea Perolehan Hak atas Tanah dan Bangunan.'),
(112, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~3', 'ayat', 'Daerah dilarang memungut pajak selain jenis Pajak sebagaimana dimaksud pada ayat (1) dan ayat (2).'),
(113, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~4', 'ayat', 'Jenis Pajak sebagaimana dimaksud pada ayat (1) dan ayat (2) dapat tidak dipungut apabila potensinya kurang memadai dan/atau disesuaikan dengan kebijakan Daerah yang ditetapkan dengan Peraturan Daerah.'),
(114, 1, 'bab~ii bagian~kesatu~jenis~pajak pasal~2 ayat~5', 'ayat', 'Khusus untuk Daerah yang setingkat dengan daerah provinsi, tetapi tidak terbagi dalam daerah kabupaten/kota otonom, seperti Daerah Khusus Ibukota Jakarta, jenis Pajak yang dapat dipungut merupakan gabungan dari Pajak untuk daerah provinsi dan Pajak untuk daerah kabupaten/kota.'),
(115, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~3 ayat~1', 'ayat', 'Objek Pajak Kendaraan Bermotor adalah kepemilikan dan/atau penguasaan Kendaraan Bermotor.'),
(116, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~3 ayat~2', 'ayat', 'Termasuk dalam pengertian Kendaraan Bermotor sebagaimana dimaksud pada ayat (1) adalah kendaraan bermotor beroda beserta gandengannya, yang dioperasikan di semua jenis jalan darat dan kendaraan bermotor yang dioperasikan di air dengan ukuran isi kotor GT 5 (lima Gross Tonnage) sampai dengan GT 7 (tujuh Gross Tonnage).'),
(117, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~3 ayat~3', 'ayat', 'Dikecualikan dari pengertian Kendaraan Bermotor sebagaimana dimaksud pada ayat (2) adalah:'),
(118, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~3 ayat~3 huruf~a', 'huruf', 'kereta api;'),
(119, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~3 ayat~3 huruf~b', 'huruf', 'Kendaraan Bermotor yang semata-mata digunakan untuk keperluan pertahanan dan keamanan negara;'),
(120, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~3 ayat~3 huruf~c', 'huruf', 'Kendaraan Bermotor yang dimiliki dan/atau dikuasai kedutaan, konsulat, perwakilan negara asing dengan asas timbal balik dan lembaga-lembaga internasional yang memperoleh fasilitas pembebasan pajak dari Pemerintah; dan'),
(121, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~3 ayat~3 huruf~d', 'huruf', 'objek Pajak lainnya yang ditetapkan dalam Peraturan Daerah.'),
(122, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~4 ayat~1', 'ayat', 'Subjek Pajak Kendaraan Bermotor adalah orang pribadi atau Badan yang memiliki dan/atau menguasai Kendaraan Bermotor.'),
(123, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~4 ayat~2', 'ayat', 'Wajib Pajak Kendaraan Bermotor adalah orang pribadi atau Badan yang memiliki Kendaraan Bermotor.'),
(124, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~4 ayat~3', 'ayat', 'Dalam hal Wajib Pajak Badan, kewajiban perpajakannya diwakili oleh pengurus atau kuasa Badan tersebut.'),
(125, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~1', 'ayat', 'Dasar pengenaan Pajak Kendaraan Bermotor adalah hasil perkalian dari 2 (dua) unsur pokok:'),
(126, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~1 huruf~a', 'huruf', 'Nilai Jual Kendaraan Bermotor; dan'),
(127, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~1 huruf~b', 'huruf', 'bobot yang mencerminkan secara relatif tingkat kerusakan jalan dan/atau pencemaran lingkungan akibat penggunaan Kendaraan Bermotor.'),
(128, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~2', 'ayat', 'Khusus untuk Kendaraan Bermotor yang digunakan di luar jalan umum, termasuk alat-alat berat dan alat- alat besar serta kendaraan di air, dasar pengenaan Pajak Kendaraan Bermotor adalah Nilai Jual Kendaraan Bermotor.'),
(129, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~3', 'ayat', 'Bobot sebagaimana dimaksud pada ayat (1) huruf b dinyatakan dalam koefisien yang nilainya 1 (satu) atau lebih besar dari 1 (satu), dengan pengertian sebagai berikut:'),
(130, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~3 huruf~a', 'huruf', 'koefisien sama dengan 1 (satu) berarti kerusakan jalan dan/atau pencemaran lingkungan oleh penggunaan Kendaraan Bermotor tersebut dianggap masih dalam batas toleransi; dan'),
(131, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~3 huruf~b', 'huruf', 'koefisien lebih besar dari 1 (satu) berarti penggunaan Kendaraan Bermotor tersebut dianggap melewati batas toleransi.'),
(132, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~4', 'ayat', 'Nilai Jual Kendaraan Bermotor ditentukan berdasarkan Harga Pasaran Umum atas suatu Kendaraan Bermotor.'),
(133, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~5', 'ayat', 'Harga Pasaran Umum sebagaimana dimaksud pada ayat (4) adalah harga rata-rata yang diperoleh dari berbagai sumber data yang akurat.'),
(134, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~6', 'ayat', 'Nilai Jual Kendaraan Bermotor sebagaimana dimaksud pada ayat (4) ditetapkan berdasarkan Harga Pasaran Umum pada minggu pertama bulan Desember Tahun Pajak sebelumnya.'),
(135, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~7', 'ayat', 'Dalam hal Harga Pasaran Umum suatu Kendaraan Bermotor tidak diketahui, Nilai Jual Kendaraan Bermotor dapat ditentukan berdasarkan sebagian atau seluruh faktor-faktor:'),
(136, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~7 huruf~a', 'huruf', 'harga Kendaraan Bermotor dengan isi silinder dan/atau satuan tenaga yang sama;'),
(137, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~7 huruf~b', 'huruf', 'penggunaan Kendaraan Bermotor untuk umum atau pribadi;'),
(138, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~7 huruf~c', 'huruf', 'harga Kendaraan Bermotor dengan merek Kendaraan Bermotor yang sama;'),
(139, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~7 huruf~d', 'huruf', 'harga Kendaraan Bermotor dengan tahun pembuatan Kendaraan Bermotor yang sama;'),
(140, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~7 huruf~e', 'huruf', 'harga Kendaraan Bermotor dengan pembuat Kendaraan Bermotor;'),
(141, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~7 huruf~f', 'huruf', 'harga Kendaraan Bermotor dengan Kendaraan Bermotor sejenis; dan'),
(142, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~7 huruf~g', 'huruf', 'harga Kendaraan Bermotor berdasarkan dokumen Pemberitahuan Impor Barang (PIB).'),
(143, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~8', 'ayat', 'Bobot sebagaimana dimaksud pada ayat (1) huruf b dihitung berdasarkan faktor-faktor:'),
(144, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~8 huruf~a', 'huruf', 'tekanan gandar, yang dibedakan atas dasar jumlah sumbu/as, roda, dan berat Kendaraan Bermotor;'),
(145, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~8 huruf~b', 'huruf', 'jenis bahan bakar Kendaraan Bermotor yang dibedakan menurut solar, bensin, gas, listrik, tenaga surya, atau jenis bahan bakar lainnya; dan'),
(146, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~8 huruf~c', 'huruf', 'jenis, penggunaan, tahun pembuatan, dan ciri-ciri mesin Kendaraan Bermotor yang dibedakan berdasarkan jenis mesin 2 tak atau 4 tak, dan isi silinder.'),
(147, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~9', 'ayat', 'Penghitungan dasar pengenaan Pajak Kendaraan Bermotor sebagaimana dimaksud pada ayat (1), ayat (2), ayat (3), ayat (4), ayat (5), ayat (6), ayat (7), dan ayat (8) dinyatakan dalam suatu tabel yang ditetapkan dengan Peraturan Menteri Dalam Negeri setelah mendapat pertimbangan dari Menteri Keuangan.'),
(148, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~5 ayat~10', 'ayat', 'Penghitungan dasar pengenaan Pajak Kendaraan Bermotor sebagaimana dimaksud pada ayat (9) ditinjau kembali setiap tahun.'),
(149, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~6 ayat~1', 'ayat', 'Tarif Pajak Kendaraan Bermotor pribadi ditetapkan sebagai berikut:'),
(150, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~6 ayat~1 huruf~a', 'huruf', 'untuk kepemilikan Kendaraan Bermotor pertama paling rendah sebesar 1% (satu persen) dan paling tinggi sebesar 2% (dua persen);'),
(151, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~6 ayat~1 huruf~b', 'huruf', 'untuk kepemilikan Kendaraan Bermotor kedua dan seterusnya tarif dapat ditetapkan secara progresif paling rendah sebesar 2% (dua persen) dan paling tinggi sebesar 10% (sepuluh persen).'),
(152, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~6 ayat~2', 'ayat', 'Kepemilikan Kendaraan Bermotor didasarkan atas nama dan/atau alamat yang sama.'),
(153, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~6 ayat~3', 'ayat', 'Tarif Pajak Kendaraan Bermotor angkutan umum, ambulans, pemadam kebakaran, sosial keagamaan, lembaga sosial dan keagamaan, Pemerintah/TNI/POLRI, Pemerintah Daerah, dan kendaraan lain yang ditetapkan dengan Peraturan Daerah, ditetapkan paling rendah sebesar 0,5% (nol koma lima persen) dan paling tinggi sebesar 1% (satu persen).'),
(154, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~6 ayat~4', 'ayat', 'Tarif Pajak Kendaraan Bermotor alat-alat berat dan alat-alat besar ditetapkan paling rendah sebesar 0,1% (nol koma satu persen) dan paling tinggi sebesar 0,2% (nol koma dua persen).'),
(155, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~6 ayat~5', 'ayat', 'Tarif Pajak Kendaraan Bermotor ditetapkan dengan Peraturan Daerah.'),
(156, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~7 ayat~1', 'ayat', 'Besaran pokok Pajak Kendaraan Bermotor yang terutang dihitung dengan cara mengalikan tarif sebagaimana dimaksud dalam<span class=v_c_link popup-content id=popup-content data-document=0dad7117-9bd2-11eb-972d-a3c4a879b523 data-article=pasal 6>Pasal 6</span>ayat (5) dengan dasar pengenaan pajak sebagaimana dimaksud dalam<span class=v_c_link popup-content id=popup-content data-document=0dad7117-9bd2-11eb-972d-a3c4a879b523 data-article=pasal 5>Pasal 5</span>ayat (9).'),
(157, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~7 ayat~2', 'ayat', 'Pajak Kendaraan Bermotor yang terutang dipungut di wilayah daerah tempat Kendaraan Bermotor terdaftar.'),
(158, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~7 ayat~3', 'ayat', 'Pemungutan Pajak Kendaraan Bermotor dilakukan bersamaan dengan penerbitan Surat Tanda Nomor Kendaraan Bermotor.'),
(159, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~7 ayat~4', 'ayat', 'Pemungutan pajak tahun berikutnya dilakukan di kas daerah atau bank yang ditunjuk oleh Kepala Daerah.'),
(160, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~8 ayat~1', 'ayat', 'Pajak Kendaraan Bermotor dikenakan untuk Masa Pajak 12 (dua belas) bulan berturut-turut terhitung mulai saat pendaftaran Kendaraan Bermotor.'),
(161, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~8 ayat~2', 'ayat', 'Pajak Kendaraan Bermotor dibayar sekaligus di muka.'),
(162, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~8 ayat~3', 'ayat', 'Untuk Pajak Kendaraan Bermotor yang karena keadaan kahar (force majeure) Masa Pajaknya tidak sampai 12 (dua belas) bulan, dapat dilakukan restitusi atas pajak yang sudah dibayar untuk porsi Masa Pajak yang belum dilalui.'),
(163, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~8 ayat~4', 'ayat', 'Ketentuan lebih lanjut mengenai tata cara pelaksanaan restitusi diatur dengan Peraturan Gubernur.'),
(164, 1, 'bab~ii bagian~kedua~pajak~kendaraan~bermotor pasal~8 ayat~5', 'ayat', 'Hasil penerimaan Pajak Kendaraan Bermotor paling sedikit 10% (sepuluh persen), termasuk yang dibagihasilkan kepada kabupaten/kota, dialokasikan untuk pembangunan dan/atau pemeliharaan jalan serta peningkatan moda dan sarana transportasi umum.'),
(165, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~1', 'ayat', 'Objek Pajak Bea Balik Nama Kendaraan Bermotor adalah penyerahan kepemilikan Kendaraan Bermotor.'),
(166, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~2', 'ayat', 'Termasuk dalam pengertian Kendaraan Bermotor sebagaimana dimaksud pada ayat (1) adalah kendaraan bermotor beroda beserta gandengannya, yang dioperasikan di semua jenis jalan darat dan kendaraan bermotor yang dioperasikan di air dengan ukuran isi kotor GT 5 (lima Gross Tonnage) sampai dengan GT 7 (tujuh Gross Tonnage).'),
(167, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~3', 'ayat', 'Dikecualikan dari pengertian Kendaraan Bermotor sebagaimana dimaksud pada ayat (2):'),
(168, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~3 huruf~a', 'huruf', 'kereta api;'),
(169, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~3 huruf~b', 'huruf', 'Kendaraan Bermotor yang semata-mata digunakan untuk keperluan pertahanan dan keamanan negara;'),
(170, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~3 huruf~c', 'huruf', 'Kendaraan Bermotor yang dimiliki dan/atau dikuasai kedutaan, konsulat, perwakilan negara asing dengan asas timbal balik dan lembaga-lembaga internasional yang memperoleh fasilitas pembebasan pajak dari Pemerintah; dan'),
(171, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~3 huruf~d', 'huruf', 'objek pajak lainnya yang ditetapkan dalam Peraturan Daerah.'),
(172, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~4', 'ayat', 'Penguasaan Kendaraan Bermotor melebihi 12 (dua belas) bulan dapat dianggap sebagai penyerahan.'),
(173, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~5', 'ayat', 'Penguasaan Kendaraan Bermotor sebagaimana dimaksud pada ayat (4) tidak termasuk penguasaan Kendaraan Bermotor karena perjanjian sewa beli.'),
(174, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~6', 'ayat', 'Termasuk penyerahan Kendaraan Bermotor sebagaimana dimaksud pada ayat (1) adalah pemasukan Kendaraan Bermotor dari luar negeri untuk dipakai secara tetap di Indonesia, kecuali:'),
(175, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~6 huruf~a', 'huruf', 'untuk dipakai sendiri oleh orang pribadi yang bersangkutan;'),
(176, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~6 huruf~b', 'huruf', 'untuk diperdagangkan;'),
(177, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~6 huruf~c', 'huruf', 'untuk dikeluarkan kembali dari wilayah pabean Indonesia; dan'),
(178, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~6 huruf~d', 'huruf', 'digunakan untuk pameran, penelitian, contoh, dan kegiatan olahraga bertaraf internasional.'),
(179, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~9 ayat~7', 'ayat', 'Pengecualian sebagaimana dimaksud pada ayat (6) huruf c tidak berlaku apabila selama 3 (tiga) tahun berturut-turut tidak dikeluarkan kembali dari wilayah pabean Indonesia.'),
(180, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~10 ayat~1', 'ayat', 'Subjek Pajak Bea Balik Nama Kendaraan Bermotor adalah orang pribadi atau Badan yang dapat menerima penyerahan Kendaraan Bermotor.'),
(181, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~10 ayat~2', 'ayat', 'Wajib Pajak Bea Balik Nama Kendaraan Bermotor adalah orang pribadi atau Badan yang menerima penyerahan Kendaraan Bermotor.'),
(182, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~11', 'pasal', 'Dasar pengenaan Bea Balik Nama Kendaraan Bermotor adalah Nilai Jual Kendaraan Bermotor sebagaimana dimaksud dalam<span class=v_c_link popup-content id=popup-content data-document=0dad7117-9bd2-11eb-972d-a3c4a879b523 data-article=pasal 5>Pasal 5</span>ayat (9).'),
(183, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~12 ayat~1', 'ayat', 'Tarif Bea Balik Nama Kendaraan Bermotor ditetapkan paling tinggi masing-masing sebagai berikut:'),
(184, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~12 ayat~1 huruf~a', 'huruf', 'penyerahan pertama sebesar 20% (dua puluh persen); dan'),
(185, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~12 ayat~1 huruf~b', 'huruf', 'penyerahan kedua dan seterusnya sebesar 1% (satu persen).'),
(186, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~12 ayat~2', 'ayat', 'Khusus untuk Kendaraan Bermotor alat-alat berat dan alat-alat besar yang tidak menggunakan jalan umum tarif pajak ditetapkan paling tinggi masing-masing sebagai berikut:'),
(187, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~12 ayat~2 huruf~a', 'huruf', 'penyerahan pertama sebesar 0,75% (nol koma tujuh puluh lima persen); dan'),
(188, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~12 ayat~2 huruf~b', 'huruf', 'penyerahan kedua dan seterusnya sebesar 0,075% (nol koma nol tujuh puluh lima persen).'),
(189, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~12 ayat~3', 'ayat', 'Tarif Bea Balik Nama Kendaraan Bermotor ditetapkan dengan Peraturan Daerah.'),
(190, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~13 ayat~1', 'ayat', 'Besaran Pokok Pajak Bea Balik Nama Kendaraan Bermotor yang terutang dihitung dengan cara mengalikan tarif sebagaimana dimaksud dalam<span class=v_c_link popup-content id=popup-content data-document=0dad7117-9bd2-11eb-972d-a3c4a879b523 data-article=pasal 12>Pasal 12</span>ayat (3) dengan dasar pengenaan pajak sebagaimana dimaksud dalam Pasal 11.'),
(191, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~13 ayat~2', 'ayat', 'Bea Balik Nama Kendaraan Bermotor yang terutang dipungut di wilayah daerah tempat Kendaraan Bermotor terdaftar.'),
(192, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~13 ayat~3', 'ayat', 'Pembayaran Bea Balik Nama Kendaraan Bermotor dilakukan pada saat pendaftaran.'),
(193, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~14', 'pasal', 'Wajib Pajak Bea Balik Nama Kendaraan Bermotor wajib mendaftarkan penyerahan Kendaraan Bermotor dalam jangka waktu paling lambat 30 (tiga puluh) hari kerja sejak saat penyerahan.'),
(194, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~15 ayat~1', 'ayat', 'Orang pribadi atau Badan yang menyerahkan Kendaraan Bermotor melaporkan secara tertulis penyerahan tersebut kepada gubernur atau pejabat yang ditunjuk dalam jangka waktu 30 (tiga puluh) hari kerja sejak saat penyerahan.'),
(195, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~15 ayat~2', 'ayat', 'Laporan tertulis sebagaimana dimaksud pada ayat (1), paling sedikit berisi:'),
(196, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~15 ayat~2 huruf~a', 'huruf', 'nama dan alamat orang pribadi atau Badan yang menerima penyerahan;'),
(197, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~15 ayat~2 huruf~b', 'huruf', 'tanggal, bulan, dan tahun penyerahan;'),
(198, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~15 ayat~2 huruf~c', 'huruf', 'nomor polisi kendaraan bermotor;'),
(199, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~15 ayat~2 huruf~d', 'huruf', 'lampiran fotokopi Surat Tanda Nomor Kendaraan Bermotor; dan'),
(200, 1, 'bab~ii bagian~ketiga~bea~balik~nama~kendaraan~bermotor pasal~15 ayat~2 huruf~e', 'huruf', 'khusus untuk kendaraan di air ditambahkan pas dan nomor pas kapal.'),
(201, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~16', 'pasal', 'Objek Pajak Bahan Bakar Kendaraan Bermotor adalah Bahan Bakar Kendaraan Bermotor yang disediakan atau dianggap digunakan untuk kendaraan bermotor, termasuk bahan bakar yang digunakan untuk kendaraan di air.'),
(202, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~17 ayat~1', 'ayat', 'Subjek Pajak Bahan Bakar Kendaraan Bermotor adalah konsumen Bahan Bakar Kendaraan Bermotor.'),
(203, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~17 ayat~2', 'ayat', 'Wajib Pajak Bahan Bakar Kendaraan Bermotor adalah orang pribadi atau Badan yang menggunakan Bahan Bakar Kendaraan Bermotor.'),
(204, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~17 ayat~3', 'ayat', 'Pemungutan Pajak Bahan Bakar Kendaraan Bermotor dilakukan oleh penyedia Bahan Bakar Kendaraan Bermotor.'),
(205, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~17 ayat~4', 'ayat', 'Penyedia Bahan Bakar Kendaraan Bermotor sebagaimana dimaksud pada ayat (3) adalah produsen dan/atau importir Bahan Bakar Kendaraan Bermotor, baik untuk dijual maupun untuk digunakan sendiri.'),
(206, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~18', 'pasal', 'Dasar pengenaan Pajak Bahan Bakar Kendaraan Bermotor adalah Nilai Jual Bahan Bakar Kendaraan Bermotor sebelum dikenakan Pajak Pertambahan Nilai.'),
(207, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~19 ayat~1', 'ayat', 'Tarif Pajak Bahan Bakar Kendaraan Bermotor ditetapkan paling tinggi sebesar 10% (sepuluh persen).'),
(208, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~19 ayat~2', 'ayat', 'Khusus tarif Pajak Bahan Bakar Kendaraan Bermotor untuk bahan bakar kendaraan umum dapat ditetapkan paling sedikit 50% (lima puluh persen) lebih rendah dari tarif Pajak Bahan Bakar Kendaraan Bermotor untuk kendaraan pribadi.'),
(209, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~19 ayat~3', 'ayat', 'Pemerintah dapat mengubah tarif Pajak Bahan Bakar Kendaraan Bermotor yang sudah ditetapkan dalam Peraturan Daerah dengan Peraturan Presiden.'),
(210, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~19 ayat~4', 'ayat', 'Kewenangan Pemerintah untuk mengubah tarif Pajak Bahan Bakar Kendaraan Bermotor dilakukan dalam hal:'),
(211, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~19 ayat~4 huruf~a', 'huruf', 'terjadi kenaikan harga minyak dunia melebihi 130% (seratus tiga puluh persen) dari asumsi harga minyak dunia yang ditetapkan dalam Undang-Undang tentang Anggaran Pendapatan dan Belanja Negara tahun berjalan; atau'),
(212, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~19 ayat~4 huruf~b', 'huruf', 'diperlukan stabilisasi harga bahan bakar minyak untuk jangka waktu paling lama 3 (tiga) tahun sejak ditetapkannya Undang-Undang ini.'),
(213, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~19 ayat~5', 'ayat', 'Dalam hal harga minyak dunia sebagaimana dimaksud pada ayat (4) huruf a sudah normal kembali, Peraturan Presiden sebagaimana dimaksud pada ayat (3) dicabut dalam jangka waktu paling lama 2 (dua) bulan.'),
(214, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~19 ayat~6', 'ayat', 'Tarif Pajak Bahan Bakar Kendaraan Bermotor ditetapkan dengan Peraturan Daerah.'),
(215, 1, 'bab~ii bagian~keempat~pajak~bahan~bakar~kendaraan~bermotor pasal~20', 'pasal', 'Besaran pokok Pajak Bahan Bakar Kendaraan Bermotor yang terutang dihitung dengan cara mengalikan tarif pajak sebagaimana dimaksud dalam<span class=v_c_link popup-content id=popup-content data-document=0dad7117-9bd2-11eb-972d-a3c4a879b523 data-article=pasal 19>Pasal 19</span>ayat (6) dengan dasar pengenaan pajak sebagaimana dimaksud dalam Pasal 18.'),
(216, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~21 ayat~1', 'ayat', 'Objek Pajak Air Permukaan adalah pengambilan dan/atau pemanfaatan Air Permukaan.'),
(217, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~21 ayat~2', 'ayat', 'Dikecualikan dari objek Pajak Air Permukaan adalah:'),
(218, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~21 ayat~2 huruf~a', 'huruf', 'pengambilan dan/atau pemanfaatan Air Permukaan untuk keperluan dasar rumah tangga, pengairan pertanian dan perikanan rakyat, dengan tetap memperhatikan kelestarian lingkungan dan peraturan perundang-undangan; dan'),
(219, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~21 ayat~2 huruf~b', 'huruf', 'pengambilan dan/atau pemanfaatan Air Permukaan lainnya yang ditetapkan dalam Peraturan Daerah.'),
(220, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~22 ayat~1', 'ayat', 'Subjek Pajak Air Permukaan adalah orang pribadi atau Badan yang dapat melakukan pengambilan dan/atau pemanfaatan Air Permukaan.'),
(221, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~22 ayat~2', 'ayat', 'Wajib Pajak Air Permukaan adalah orang pribadi atau Badan yang melakukan pengambilan dan/atau pemanfaatan Air Permukaan.'),
(222, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~23 ayat~1', 'ayat', 'Dasar pengenaan Pajak Air Permukaan adalah Nilai Perolehan Air Permukaan.'),
(223, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~23 ayat~2', 'ayat', 'Nilai Perolehan Air Permukaan sebagaimana dimaksud pada ayat (1) dinyatakan dalam rupiah yang dihitung dengan mempertimbangkan sebagian atau seluruh faktor-faktor berikut:'),
(224, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~23 ayat~2 huruf~a', 'huruf', 'jenis sumber air;'),
(225, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~23 ayat~2 huruf~b', 'huruf', 'lokasi sumber air;'),
(226, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~23 ayat~2 huruf~c', 'huruf', 'tujuan pengambilan dan/atau pemanfaatan air;'),
(227, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~23 ayat~2 huruf~d', 'huruf', 'volume air yang diambil dan/atau dimanfaatkan;'),
(228, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~23 ayat~2 huruf~e', 'huruf', 'kualitas air;'),
(229, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~23 ayat~2 huruf~f', 'huruf', 'luas areal tempat pengambilan dan/atau pemanfaatan air; dan'),
(230, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~23 ayat~2 huruf~g', 'huruf', 'tingkat kerusakan lingkungan yang diakibatkan oleh pengambilan dan/atau pemanfaatan air.'),
(231, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~23 ayat~3', 'ayat', 'Penggunaan faktor-faktor sebagaimana dimaksud pada ayat (2) disesuaikan dengan kondisi masing- masing Daerah.');
INSERT INTO `tbl_uu_pasal` (`id`, `id_tbl_uu`, `uud_id`, `uud_section`, `uud_content`) VALUES
(232, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~23 ayat~4', 'ayat', 'Besarnya Nilai Perolehan Air Permukaan sebagaimana dimaksud pada ayat (1) ditetapkan dengan Peraturan Gubernur.'),
(233, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~24 ayat~1', 'ayat', 'Tarif Pajak Air Permukaan ditetapkan paling tinggi sebesar 10% (sepuluh persen).'),
(234, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~24 ayat~2', 'ayat', 'Tarif Pajak Air Permukaan ditetapkan dengan Peraturan Daerah.'),
(235, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~25 ayat~1', 'ayat', 'Besaran pokok Pajak Air Permukaan yang terutang dihitung dengan cara mengalikan tarif sebagaimana dimaksud dalam<span class=v_c_link popup-content id=popup-content data-document=0dad7117-9bd2-11eb-972d-a3c4a879b523 data-article=pasal 24>Pasal 24</span>ayat (2) dengan dasar pengenaan pajak sebagaimana dimaksud dalam<span class=v_c_link popup-content id=popup-content data-document=0dad7117-9bd2-11eb-972d-a3c4a879b523 data-article=pasal 23>Pasal 23</span>ayat (4).'),
(236, 1, 'bab~ii bagian~kelima~pajak~air~permukaan pasal~25 ayat~2', 'ayat', 'Pajak Air Permukaan yang terutang dipungut di wilayah daerah tempat air berada.'),
(237, 1, 'bab~ii bagian~keenam~pajak~rokok pasal~26 ayat~1', 'ayat', 'Objek Pajak Rokok adalah konsumsi rokok.'),
(238, 1, 'bab~ii bagian~keenam~pajak~rokok pasal~26 ayat~2', 'ayat', 'Rokok sebagaimana dimaksud pada ayat (1) meliputi sigaret, cerutu, dan rokok daun.'),
(239, 1, 'bab~ii bagian~keenam~pajak~rokok pasal~26 ayat~3', 'ayat', 'Dikecualikan dari objek Pajak Rokok sebagaimana dimaksud pada ayat (1) adalah rokok yang tidak dikenai cukai berdasarkan peraturan perundang-undangan di bidang cukai.'),
(240, 1, 'bab~ii bagian~keenam~pajak~rokok pasal~27 ayat~1', 'ayat', 'Subjek Pajak Rokok adalah konsumen rokok.'),
(241, 1, 'bab~ii bagian~keenam~pajak~rokok pasal~27 ayat~2', 'ayat', 'Wajib Pajak Rokok adalah pengusaha pabrik rokok/produsen dan importir rokok yang memiliki izin berupa Nomor Pokok Pengusaha Barang Kena Cukai.'),
(242, 1, 'bab~ii bagian~keenam~pajak~rokok pasal~27 ayat~3', 'ayat', 'Pajak Rokok dipungut oleh instansi Pemerintah yang berwenang memungut cukai bersamaan dengan pemungutan cukai rokok.'),
(243, 1, 'bab~ii bagian~keenam~pajak~rokok pasal~27 ayat~4', 'ayat', 'Pajak Rokok yang dipungut oleh instansi Pemerintah sebagaimana dimaksud pada ayat (3) disetor ke rekening kas umum daerah provinsi secara proporsional berdasarkan jumlah penduduk.'),
(244, 1, 'bab~ii bagian~keenam~pajak~rokok pasal~27 ayat~5', 'ayat', 'Ketentuan lebih lanjut mengenai tata cara pemungutan dan penyetoran Pajak Rokok diatur dengan Peraturan Menteri Keuangan.'),
(245, 1, 'bab~ii bagian~keenam~pajak~rokok pasal~28', 'pasal', 'Dasar pengenaan Pajak Rokok adalah cukai yang ditetapkan oleh Pemerintah terhadap rokok.'),
(246, 1, 'bab~ii bagian~keenam~pajak~rokok pasal~29', 'pasal', 'Tarif Pajak Rokok ditetapkan sebesar 10% (sepuluh persen) dari cukai rokok.'),
(247, 1, 'bab~ii bagian~keenam~pajak~rokok pasal~30', 'pasal', 'Besaran pokok Pajak Rokok yang terutang dihitung dengan cara mengalikan tarif pajak sebagaimana dimaksud dalam<span class=v_c_link popup-content id=popup-content data-document=0dad7117-9bd2-11eb-972d-a3c4a879b523 data-article=pasal 29>Pasal 29</span>dengan dasar pengenaan pajak sebagaimana dimaksud dalam Pasal 28.'),
(248, 1, 'bab~ii bagian~keenam~pajak~rokok pasal~31', 'pasal', 'Penerimaan Pajak Rokok, baik bagian provinsi maupun bagian kabupaten/kota, dialokasikan paling sedikit 50% (lima puluh persen) untuk mendanai pelayanan kesehatan masyarakat dan penegakan hukum oleh aparat yang berwenang.'),
(249, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~32 ayat~1', 'ayat', 'Objek Pajak Hotel adalah pelayanan yang disediakan oleh Hotel dengan pembayaran, termasuk jasa penunjang sebagai kelengkapan Hotel yang sifatnya memberikan kemudahan dan kenyamanan, termasuk fasilitas olahraga dan hiburan.'),
(250, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~32 ayat~2', 'ayat', 'Jasa penunjang sebagaimana dimaksud pada ayat (1) adalah fasilitas telepon, faksimile, teleks, internet, fotokopi, pelayanan cuci, seterika, transportasi, dan fasilitas sejenis lainnya yang disediakan atau dikelola Hotel.'),
(251, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~32 ayat~3', 'ayat', 'Tidak termasuk objek Pajak Hotel sebagaimana dimaksud pada ayat (1) adalah:'),
(252, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~32 ayat~3 huruf~a', 'huruf', 'jasa tempat tinggal asrama yang diselenggarakan oleh Pemerintah atau Pemerintah Daerah;'),
(253, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~32 ayat~3 huruf~b', 'huruf', 'jasa sewa apartemen, kondominium, dan sejenisnya;'),
(254, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~32 ayat~3 huruf~c', 'huruf', 'jasa tempat tinggal di pusat pendidikan atau kegiatan keagamaan;'),
(255, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~32 ayat~3 huruf~d', 'huruf', 'jasa tempat tinggal di rumah sakit, asrama perawat, panti jompo, panti asuhan, dan panti sosial lainnya yang sejenis; dan'),
(256, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~32 ayat~3 huruf~e', 'huruf', 'jasa biro perjalanan atau perjalanan wisata yang diselenggarakan oleh Hotel yang dapat dimanfaatkan oleh umum.'),
(257, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~33 ayat~1', 'ayat', 'Subjek Pajak Hotel adalah orang pribadi atau Badan yang melakukan pembayaran kepada orang pribadi atau Badan yang mengusahakan Hotel.'),
(258, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~33 ayat~2', 'ayat', 'Wajib Pajak Hotel adalah orang pribadi atau Badan yang mengusahakan Hotel.'),
(259, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~34', 'pasal', 'Dasar pengenaan Pajak Hotel adalah jumlah pembayaran atau yang seharusnya dibayar kepada Hotel.'),
(260, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~35 ayat~1', 'ayat', 'Tarif Pajak Hotel ditetapkan paling tinggi sebesar 10% (sepuluh persen).'),
(261, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~35 ayat~2', 'ayat', 'Tarif Pajak Hotel ditetapkan dengan Peraturan Daerah.'),
(262, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~36 ayat~1', 'ayat', 'Besaran pokok Pajak Hotel yang terutang dihitung dengan cara mengalikan tarif sebagaimana dimaksud dalam<span class=v_c_link popup-content id=popup-content data-document=0dad7117-9bd2-11eb-972d-a3c4a879b523 data-article=pasal 35>Pasal 35</span>ayat (2) dengan dasar pengenaan pajak sebagaimana dimaksud dalam Pasal 34.'),
(263, 1, 'bab~ii bagian~ketujuh~pajak~hotel pasal~36 ayat~2', 'ayat', 'Pajak Hotel yang terutang dipungut di wilayah daerah tempat Hotel berlokasi.'),
(264, 1, 'bab~ii bagian~kedelapan~pajak~restoran pasal~37 ayat~1', 'ayat', 'Objek Pajak Restoran adalah pelayanan yang disediakan oleh Restoran.'),
(265, 1, 'bab~ii bagian~kedelapan~pajak~restoran pasal~37 ayat~2', 'ayat', 'Pelayanan yang disediakan Restoran sebagaimana dimaksud pada ayat (1) meliputi pelayanan penjualan makanan dan/atau minuman yang dikonsumsi oleh pembeli, baik dikonsumsi di tempat pelayanan maupun di tempat lain.'),
(266, 1, 'bab~ii bagian~kedelapan~pajak~restoran pasal~37 ayat~3', 'ayat', 'Tidak termasuk objek Pajak Restoran sebagaimana dimaksud pada ayat (1) adalah pelayanan yang disediakan oleh Restoran yang nilai penjualannya tidak melebihi batas tertentu yang ditetapkan dengan Peraturan Daerah.'),
(267, 1, 'bab~ii bagian~kedelapan~pajak~restoran pasal~38 ayat~1', 'ayat', 'Subjek Pajak Restoran adalah orang pribadi atau Badan yang membeli makanan dan/atau minuman dari Restoran.'),
(268, 1, 'bab~ii bagian~kedelapan~pajak~restoran pasal~38 ayat~2', 'ayat', 'Wajib Pajak Restoran adalah orang pribadi atau Badan yang mengusahakan Restoran.'),
(269, 1, 'bab~ii bagian~kedelapan~pajak~restoran pasal~39', 'pasal', 'Dasar pengenaan Pajak Restoran adalah jumlah pembayaran yang diterima atau yang seharusnya diterima Restoran.'),
(270, 1, 'bab~ii bagian~kedelapan~pajak~restoran pasal~40 ayat~1', 'ayat', 'Tarif Pajak Restoran ditetapkan paling tinggi sebesar 10% (sepuluh persen).'),
(271, 1, 'bab~ii bagian~kedelapan~pajak~restoran pasal~40 ayat~2', 'ayat', 'Tarif Pajak Restoran ditetapkan dengan Peraturan Daerah.'),
(272, 1, 'bab~ii bagian~kedelapan~pajak~restoran pasal~41 ayat~1', 'ayat', 'Besaran pokok Pajak Restoran yang terutang dihitung dengan cara mengalikan tarif sebagaimana dimaksud dalam<span class=v_c_link popup-content id=popup-content data-document=0dad7117-9bd2-11eb-972d-a3c4a879b523 data-article=pasal 40>Pasal 40</span>ayat (2) dengan dasar pengenaan pajak sebagaimana dimaksud dalam Pasal 39.'),
(273, 1, 'bab~ii bagian~kedelapan~pajak~restoran pasal~41 ayat~2', 'ayat', 'Pajak Restoran yang terutang dipungut di wilayah daerah tempat Restoran berlokasi.'),
(274, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~42 ayat~1', 'ayat', 'Objek Pajak Hiburan adalah jasa penyelenggaraan Hiburan dengan dipungut bayaran.'),
(275, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~42 ayat~2', 'ayat', 'Hiburan sebagaimana dimaksud pada ayat (1) adalah:'),
(276, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~42 ayat~2 huruf~a', 'huruf', 'tontonan film;'),
(277, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~42 ayat~2 huruf~b', 'huruf', 'pagelaran kesenian, musik, tari, dan/atau busana;'),
(278, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~42 ayat~2 huruf~c', 'huruf', 'kontes kecantikan, binaraga, dan sejenisnya;'),
(279, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~42 ayat~2 huruf~d', 'huruf', 'pameran;'),
(280, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~42 ayat~2 huruf~e', 'huruf', 'diskotik, karaoke, klab malam, dan sejenisnya;'),
(281, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~42 ayat~2 huruf~f', 'huruf', 'sirkus, akrobat, dan sulap;'),
(282, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~42 ayat~2 huruf~g', 'huruf', 'permainan bilyar, golf, dan boling;'),
(283, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~42 ayat~2 huruf~h', 'huruf', 'pacuan kuda, kendaraan bermotor, dan permainan ketangkasan;'),
(284, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~42 ayat~2 huruf~i', 'huruf', 'panti pijat, refleksi, mandi uap/spa, dan pusat kebugaran (fitness center); dan'),
(285, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~42 ayat~2 huruf~j', 'huruf', 'pertandingan olahraga.'),
(286, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~42 ayat~3', 'ayat', 'Penyelenggaraan Hiburan sebagaimana dimaksud pada ayat (2) dapat dikecualikan dengan Peraturan Daerah.'),
(287, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~43 ayat~1', 'ayat', 'Subjek Pajak Hiburan adalah orang pribadi atau Badan yang menikmati Hiburan.'),
(288, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~43 ayat~2', 'ayat', 'Wajib Pajak Hiburan adalah orang pribadi atau Badan yang menyelenggarakan Hiburan.'),
(289, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~44 ayat~1', 'ayat', 'Dasar pengenaan Pajak Hiburan adalah jumlah uang yang diterima atau yang seharusnya diterima oleh penyelenggara Hiburan.'),
(290, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~44 ayat~2', 'ayat', 'Jumlah uang yang seharusnya diterima sebagaimana dimaksud pada ayat (1) termasuk potongan harga dan tiket cuma-cuma yang diberikan kepada penerima jasa Hiburan.'),
(291, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~45 ayat~1', 'ayat', 'Tarif Pajak Hiburan ditetapkan paling tinggi sebesar 35% (tiga puluh lima persen).'),
(292, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~45 ayat~2', 'ayat', 'Khusus untuk Hiburan berupa pagelaran busana, kontes kecantikan, diskotik, karaoke, klab malam, permainan ketangkasan, panti pijat, dan mandi uap/spa, tarif Pajak Hiburan dapat ditetapkan paling tinggi sebesar 75% (tujuh puluh lima persen).'),
(293, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~45 ayat~3', 'ayat', 'Khusus Hiburan kesenian rakyat/tradisional dikenakan tarif Pajak Hiburan ditetapkan paling tinggi sebesar 10% (sepuluh persen).'),
(294, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~45 ayat~4', 'ayat', 'Tarif Pajak Hiburan ditetapkan dengan Peraturan Daerah.'),
(295, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~46 ayat~1', 'ayat', 'Besaran pokok Pajak Hiburan yang terutang dihitung dengan cara mengalikan tarif sebagaimana dimaksud dalam<span class=v_c_link popup-content id=popup-content data-document=0dad7117-9bd2-11eb-972d-a3c4a879b523 data-article=pasal 45>Pasal 45</span>ayat (4) dengan dasar pengenaan pajak sebagaimana dimaksud dalam Pasal 44.'),
(296, 1, 'bab~ii bagian~kesembilan~pajak~hiburan pasal~46 ayat~2', 'ayat', 'Pajak Hiburan yang terutang dipungut di wilayah daerah tempat Hiburan diselenggarakan.'),
(297, 1, 'bab~ii bagian~kesepuluh~pajak~reklame pasal~47 ayat~1', 'ayat', 'Objek Pajak Reklame adalah semua penyelenggaraan Reklame.'),
