(426, 1, 'bab~ii bagian~keempat~belas~pajak~air~tanah pasal~69 ayat~2 huruf~b', 'huruf', 'lokasi sumber air;'),
(427, 1, 'bab~ii bagian~keempat~belas~pajak~air~tanah pasal~69 ayat~2 huruf~c', 'huruf', 'tujuan pengambilan dan/atau pemanfaatan air;'),
(428, 1, 'bab~ii bagian~keempat~belas~pajak~air~tanah pasal~69 ayat~2 huruf~d', 'huruf', 'volume air yang diambil dan/atau dimanfaatkan;'),
(429, 1, 'bab~ii bagian~keempat~belas~pajak~air~tanah pasal~69 ayat~2 huruf~e', 'huruf', 'kualitas air; dan'),
(430, 1, 'bab~ii bagian~keempat~belas~pajak~air~tanah pasal~69 ayat~2 huruf~f', 'huruf', 'tingkat kerusakan lingkungan yang diakibatkan oleh pengambilan dan/atau pemanfaatan air.'),
